from sqlalchemy import (
    Column,
    String,
    DateTime,
    Integer,
)
from sqlalchemy.ext.declarative import declarative_base
from sqlalchemy.sql import func


base = declarative_base()


class CourseClass(base):

    __tablename__ = "course_classes"

    id = Column(Integer, primary_key=True)
    name = Column(String)
    description = Column(String)
    image = Column(String)
    start_date = Column(DateTime(timezone=False), default=func.now())
    end_date = Column(DateTime(timezone=False), default=func.now())
    schedule = Column(String)
    how_to_access = Column(String)
    course_id = Column(Integer)
    duration = Column(Integer)
    created_at = Column(DateTime(timezone=False), default=func.now())
    updated_at = Column(DateTime(timezone=False), default=func.now())
